import React from 'react';
import { Button, ButtonGroup } from '@chakra-ui/react';
import { Heading } from '@chakra-ui/react';

type HomePageProps = {
    onStartGame: () => void;
};

const HomePage: React.FC<HomePageProps> = ({ onStartGame }) => {
    const gradient = 'linear-gradient(45deg, #FF0080, #7928CA)';

    const headingStyle: React.CSSProperties = {
        color: gradient,
        WebkitBackgroundClip: 'text',
        textAlign: 'center',
        marginTop: '4',
        fontSize: '50px',
        fontFamily: 'cursive',
        textShadow: "2px 2px 4px rgba(0, 0, 0, 0.4)",
    };

    const containerStyle: React.CSSProperties = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
    };

    return (
    <div>
        <Heading as="h1" size="2xl" style={headingStyle}>
            Math Chess
        </Heading>
        <Button colorScheme="teal" size="lg" mt="8" mx="auto" display="block">
            Start
        </Button>
        <Button colorScheme="teal" size="lg" mt="4" mx="auto" display="block">
            Settings
        </Button>
    </div>
    );
};


export default HomePage;
