import React from 'react';

type SquareProps = {
    isBlack: boolean;
};

const Square: React.FC<SquareProps> = ({ isBlack }) => {
    const backgroundColor = isBlack ? 'black' : 'white';

    return <div style={{ backgroundColor }} className="square" />;
};

const Checkerboard: React.FC = () => {
    const renderSquare = (isBlack: boolean, index: number) => {
        return <Square key={index} isBlack={isBlack} />;
    };

    const renderRow = (rowIndex: number) => {
        const squares = [];

        for (let i = 0; i < 8; i++) {
            const isBlack = (rowIndex + i) % 2 === 1;
            squares.push(renderSquare(isBlack, i));
        }

        return <div key={rowIndex}>{squares}</div>;
    };

    const rows = [];

    for (let i = 0; i < 8; i++) {
        rows.push(renderRow(i));
    }

    return <div className="checkerboard">{rows}</div>;
};

export default Checkerboard;
