import React, { useEffect, useRef } from 'react';

const ChineseCheckerBoard: React.FC = () => {
    const board = useRef<number[][]>(new Array(17));

    const player1: string[] = ["#FED7E2", "#FBB6CE"];
    const player2: string[] = ["#C4F1F9", "#9DECF9"];
    const empty: string = "#FFFFFF";
    const background: string = "#E9D8FD";

    const drawPiece = (x: number, y: number, color: number) => {
        const pieceStyles: { [key: number]: React.CSSProperties } = {
            1: { backgroundColor: player1[0] },
            2: { backgroundColor: player2[0] },
            0: { backgroundColor: empty },
        };

        const style: React.CSSProperties = {
            position: "absolute",
            top: `${y * 50}px`,
            left: `${x * 50}px`,
            width: "40px",
            height: "40px",
            borderRadius: "50%",
            ...pieceStyles[color],
        };

        return <div style={style} />;
    };

    useEffect(() => {
        for (let row = 0; row < 15; row++) {
            board.current[row] = new Array(15).fill(0);
        }

        for (let row = 0; row < 15; row++) {
            for (let col = 0; col < 15; j++) {
                if ((row + j) % 2 === 1 || Math.abs(row- 8) + Math.abs(j - 8) > 8) {
                    board.current[row][j] = -1;
                } else if (row < 4) {
                    board.current[row][j] = 1;
                } else if (row > 10) {
                    board.current[row][j] = 2;
                }
            }
        }
    }, []);
    console.log(board);

    return (
        <div style={{ position: "relative", width: "100vw", height: "100vh", backgroundColor: background }}>
            {board.current.map((row, rowIndex) => (
                <div key={rowIndex} style={{ display: 'flex' }}>
                    {row.map((value, colIndex) => (
                        <div key={colIndex}>
                            {drawPiece(rowIndex, colIndex / 2, value)}
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
};

export default ChineseCheckerBoard;
