import React from 'react';
import './Chessboard.css'; // Import the CSS file

interface ChessboardProps {
    size: number;
}

const Chessboard: React.FC<ChessboardProps> = ({ size }) => {
    const renderSquare = (row: number, col: number) => {
        const isEvenSquare = (row + col) % 2 === 0;
        const squareColor = isEvenSquare ? 'light' : 'dark';

        return (
            <div className={`square ${squareColor}`} key={`${row}-${col}`} />
        );
    };

    const renderRow = (row: number) => {
        const squares = [];

        for (let col = 0; col < size; col++) {
            squares.push(renderSquare(row, col));
        }

        return <div className="row" key={row}>{squares}</div>;
    };

    const renderBoard = () => {
        const rows = [];

        for (let row = 0; row < size; row++) {
            rows.push(renderRow(row));
        }

        return <div className="board">{rows}</div>;
    };

    return (
        <div className="chessboard-container">
            {renderBoard()}
        </div>
    );
};

export default Chessboard;
